import { IGQLType, IGQLField, ISDL } from 'prisma-datamodel';
import { Normalizer } from './normalizer';
export default class ModelNameNormalizer extends Normalizer {
    normalize(model: ISDL): void;
    protected assignName(obj: IGQLType | IGQLField, newName: string): void;
    protected getNormalizedTypeName(name: string, model: ISDL): string;
    protected normalizeType(type: IGQLType, model: ISDL, forceNoRename?: boolean): void;
    protected getNormalizedFieldName(name: string, parentType: IGQLType, field: IGQLField): string | null;
    protected normalizeField(field: IGQLField, parentType: IGQLType, parentModel: ISDL): void;
}
